def nQueensChecker(a):
    for i in range(len(a)):
        if(a[i].count(True)!=1):
            return False
    for j in range(len(a[i])):
        temp=[]
        for k in range(len(a[i])):
            temp.append(a[k][j])
        if(temp.count(True)!=1):
            return False
    for i in range(len(a)):
        for j in range(len(a[i])):
            if(a[i][j]==True):
                x=i
                y=j
                while(x!=len(a)-1 and y!=len(a)-1):
                    if(a[x][y]==True  and x!=i and y!=j):
                        return False
                    x+=1
                    y+=1
                while(x!=len(a)-1 and y!=0):
                    if(a[x][y]==True  and x!=i and y!=j):
                        return False
                    x+=1
                    y-=1
    return  True
                    
                    
