def tidyprop(x):
    x=abs(x)
    std=10
    while(x):
        y=x%10
        if(y>std):
            return False
        elif(std>=y):
            std=y
        x=x//10
    return True   

def fun_nth_tidynumber(n):
    found=0
    got=0
    while(found<=n):
        got+=1
        if(tidyprop(got)):
            found+=1
    return got
