def pronic(s):
  if(s==0 or s==2 or s==6):
    return True
  k=int(s/2)
  for i in range(k):
    if(s==i*(i+1)):
      return True
  return False

def nthpronicnumber(n):
  found=0
  got=-1
  while(found<=n):
        got+=1
        if(pronic(got)):
            found+=1
  return got
