def longestdigitrun(n):
  n=abs(n)
  s=str(n)
  max=0
  prev=0
  count=1
  for i in range(len(s)-1):
    if(s[i]==s[i+1]):
      count+=1
      if(count==prev  and  len(s)-2==i):
        if(max>int(s[i])):
          max=int(s[i])
    else:
      if(count==prev):
        if(max>int(s[i])):
          max=int(s[i])
          prev=count
          count=1
      if(max<int(s[i]) and count>prev):
        max=int(s[i])
        prev=count
      count=1
  return max
