def recurse(L,v,lo,hi,l3):
  if(lo<=hi):
    mid=(lo+hi)//2
    l3.append((mid,L[mid]))
    if(L[mid]==v):
      return l3
    elif(v<L[mid]):
      return recurse(L,v,lo,mid-1,l3)
    else:
      return recurse(L,v,mid+1,hi,l3)
  else:
    return l3

def recursion_binarysearchvalues(L, v):
  return recurse(L,v,0,len(L)-1,[])
