def recurse(l,l1,l2):
  if(len(l)==0):
    return l2
  else:
    if(l[0]>l1):
      l2=l1
      l1=l[0]
    elif(l2<l[0]):
      l2=l[0]
    return recurse(l[1:],l1,l2)


def recursion_secondlargest(L):
  l=len(L)
  if(l==0 or l==1):
    return None
  else:
    return recurse(L,-99999,-99999)
