def fun_isfactorish(n):
    t=n
    if n<0:
        n=-n
    if  (noOfDigits(n)) != 3:
        return False    
    
    d1 = n%10
    n//=10
    d2 = n%10
    n//=10
    d3 = n%10
 
    if(d1==0 or d2==0 or d3==0):
        return False
    if(d1==d2 or d2==d3 or d3==d1):
        return False
    if(t%d1==0 or t%d2==0 or t%d3==0):
        return True
    return False
 
def noOfDigits(n):
    c=0
    while n>0:
        n//=10
        c+=1
    return c
