  return s[::-1]

def samechars(s1, s2):
  if(type(s1)!=type('the') or type(s2)!=type('the')):
    return  False
  elif(s1[:]==s2[:]):
    return True
  elif(s1.count(s2)>0 or reversing(s1).count(s2)>0):
    return True
