def getallpermutations(x):
  if(len(x)==0):
    return []
  elif(len(x)==1):
    return x
  l=[]
  for i in range(len(x)):
    s=tuple(x[i])
    rem=x[:i]+x[i+1:]
    for j in getallpermutations(rem):
      j=tuple(j)
      t=s+j
      l.append(t)
  return l
